import { getDatabase, ref, set } from "firebase/database";

function GameOver({
  pointCount,
  setGameStarted,
  setGameOver,
  setPointCount,
  setNickname,
  nickname,
}) {
  let startGame = function (e) {
    setGameStarted(false);
    setGameOver(null);
    setPointCount(0);
  };

  function writeUserData(nickname) {
    const database = getDatabase();
    set(ref(database, "scores/"), {
      nickname: nickname,
      // scores: scores,
    });
  }

  return (
    <div id="gameOver">
      <h1>GAME OVER</h1>
      <h1>{pointCount} POINTS</h1>
      <button onClick={startGame}>
        <h1>RESTART GAME</h1>
      </button>
      <button>
        <h1>SAVE SCORE</h1>
        <input
          placeholder="Enter your name"
          // onChange={(e) => setNickname(e.target.value)}
          onChange={(e) => setNickname(e.target.value)}
        />
        <br />
        <button onClick={writeUserData}>PUSH</button>

        <input id="nickInput" placeholder="ENTER NICKNAME"></input>
      </button>
    </div>
  );
}

export default GameOver;
