import "./App.css";
import React, { useState, useEffect } from "react";
import Game from "./component/Game";
import Welcome from "./component/Welcome";
import GameOver from "./component/GameOver";
import HighScores from "./component/HighScores";
import app from "./firebase";
import db from "./firebase";
import { getFirestore, collection, getDocs } from "firebase/firestore";
import { clearGlobalAppDefaultCred } from "firebase-admin/lib/app/credential-factory";
import { getDatabase, ref, set } from "firebase/database";

function App() {
  const [gameStarted, setGameStarted] = useState(false);
  const [pointCount, setPointCount] = useState(0);
  const [gameOver, setGameOver] = useState("");
  const [scores, setScores] = useState([]);
  const [nickname, setNickname] = useState("");

  // setNickname("TESTING");

  // const Push = () => {
  //   db.ref("user")
  //     .set({
  //       nickname: nickname,
  //     })
  //     .catch(alert);
  // };

  function writeUserData(nickname) {
    const database = getDatabase();
    set(ref(database, "scores/"), {
      nickname: nickname,
      // scores: scores,
    });
  }

  const scoreList = "BEFORE";

  useEffect(async () => {
    console.log(await getCities());
    scoreList = await getCities();
    setScores(scoreList);
    console.log("score list : " + scoreList);
  }, []);

  console.log(scoreList + "SCORE LIST");
  console.log(scores + "SCORES");

  async function getCities() {
    const citiesCol = collection(db, "scores");
    const citySnapshot = await getDocs(citiesCol);
    const cityList = citySnapshot.docs.map((doc) => doc.data());
    return cityList;
    // setScores(cityList);
  }

  // db.collection("scores")
  //   .get()
  //   .then((snapshot) => {
  //     snapshot.docs.forEach((doc) => {
  //       console.log(doc);
  //     });
  //   });

  return (
    <div id="mainDiv">
      <center>
        <input
          placeholder="Enter your name"
          // value={name}
          // onChange={(e) => setNickname(e.target.value)}
          onChange={(e) => setNickname(e.target.value)}
        />
        <br />
        <button onClick={writeUserData}>PUSH</button>
      </center>
      {/* <h1>Scores</h1>
      {scores.map((score) => (
        <div key={score.id}>
          <h2>{score.nickname}</h2>
        </div>
      ))} */}
      <HighScores />
      {!gameStarted && !gameOver && (
        <Welcome
          gameStarted={gameStarted}
          setGameStarted={setGameStarted}
          gameOver={gameOver}
        />
      )}
      {gameStarted && !gameOver && (
        <Game
          pointCount={pointCount}
          setPointCount={setPointCount}
          setGameOver={setGameOver}
          gameOver={gameOver}
        />
      )}
      {gameOver && (
        <GameOver
          pointCount={pointCount}
          setPointCount={setPointCount}
          setGameStarted={setGameStarted}
          setGameOver={setGameOver}
        />
      )}
    </div>
  );
}

export default App;
