import "./App.css";
import React, { useState, useEffect } from "react";
import Game from "./component/Game";
import Welcome from "./component/Welcome";
import GameOver from "./component/GameOver";
import HighScores from "./component/HighScores";
import app from "./firebase";
import db from "./firebase";
import {
  getFirestore,
  collection,
  getDocs,
  doc,
  setDoc,
} from "firebase/firestore";
import { getDatabase, ref, set } from "firebase/database";
import { clearGlobalAppDefaultCred } from "firebase-admin/lib/app/credential-factory";
const form = document.querySelector("#add-score-form");

function App() {
  const [gameStarted, setGameStarted] = useState(false);
  const [pointCount, setPointCount] = useState(0);
  const [gameOver, setGameOver] = useState("");
  const [scores, setScores] = useState([]);
  const [nickname, setNickname] = useState("");

  const push = () => {
    // await setDoc(doc(db, "scores"), {
    //   name: { nickname },
    //   score: { pointCount },
    // });

    console.log(nickname + " : Nickname State");
    console.log(pointCount + " : Point Count State");

    // db.ref("scores")
    //   .set({
    //     nickname: nickname,
    //     score: pointCount,
    //   })
    //   .catch(alert);
  };

  // useEffect(() => {
  //   fetchScores();
  // }, []);

  // const fetchScores = async () => {
  //   const response = db.collection("scores");
  // };

  useEffect(async () => {
    const res = await getCities();
    setScores(res);
  }, []);

  const database = getDatabase();

  function writeUserData(nickname, pointCount) {
    const db = getDatabase();
    set(ref(db, "scores/"), {
      nickname: nickname,
      pointCount: pointCount,
    });
  }

  // const form = document.querySelector("#add-score-form");
  // console.log(form + " is form");
  // form.addEventListener("submit", (e) => {
  //   e.preventDefault();
  //   db.collection("scores").add({
  //     name: form.newName.value,
  //     score: form.newScore.value,
  //   });
  // });

  // useEffect(() => {
  //   console.log("SCORES");
  //   console.log(scores);
  //   console.log("score list : " + scores[0]?.score);
  //   setScores(res?.data?.scores?.id);
  // }, [scores]);

  async function getCities() {
    const citiesCol = collection(db, "scores");
    const citySnapshot = await getDocs(citiesCol);
    const cityList = citySnapshot.docs.map((doc) => doc.data());
    return cityList;
  }

  // db.collection("scores")
  //   .get()
  //   .then((snapshot) => {
  //     snapshot.docs.forEach((doc) => {
  //       console.log(doc);
  //     });
  //   });

  return (
    <div id="mainDiv">
      <HighScores
        scores={scores}
        setNickname={setNickname}
        nickname={nickname}
        writeUserData={writeUserData}
      />
      {!gameStarted && !gameOver && (
        <Welcome
          gameStarted={gameStarted}
          setGameStarted={setGameStarted}
          gameOver={gameOver}
        />
      )}
      {gameStarted && !gameOver && (
        <Game
          pointCount={pointCount}
          setPointCount={setPointCount}
          setGameOver={setGameOver}
          gameOver={gameOver}
        />
      )}
      {gameOver && (
        <GameOver
          pointCount={pointCount}
          setPointCount={setPointCount}
          setGameStarted={setGameStarted}
          setGameOver={setGameOver}
          setNickname={setNickname}
          push={push}
        />
      )}
    </div>
  );
}

export default App;
