import { getDatabase, ref, set, push } from "firebase/database";

function GameOver({
  pointCount,
  setGameStarted,
  setGameOver,
  setPointCount,
  setNickname,
  nickname,
  writeUserData,
  push,
}) {
  let startGame = function (e) {
    setGameStarted(false);
    setGameOver(null);
    setPointCount(0);
  };

  function writeUserData(nickname) {
    const database = getDatabase();
    set(ref(database, "scores/"), {
      nickname: nickname,
      // scores: scores,
    });
  }

  return (
    <div id="gameOver">
      <h1>GAME OVER</h1>
      <h1>{pointCount} POINTS</h1>
      <button onClick={startGame}>
        <h1>RESTART GAME</h1>
      </button>
      <br></br>
      <br></br>
      <input
        type="text"
        name="newNick"
        id="nickInput"
        placeholder="YOUR NAME"
        onChange={(e) => setNickname(e.target.value)}
      />
      <br></br>
      <button onClick={writeUserData}>
        <h1>SAVE SCORE</h1>
      </button>
    </div>
  );
}

export default GameOver;
